import React from "react";
import styled from "styled-components";
import { motion } from 'framer-motion';
import Me from '../assets/Images/profile(1).png';

const Box = styled(motion.div)`
position: absolute;
left: 50%;
top: 50%;
transform: translate(-50%, -50%);

width: 65vw;
height:55vh;
display: flex;

background: linear-gradient(
    to right,
    ${props => props.theme.body} 50%,
    ${props => props.theme.text} 50%) bottom,
    linear-gradient(
    to right,
    ${props => props.theme.body} 50%,
    ${props => props.theme.text} 50%) top;

    background-repeat: no-repeat;
    background-size: 100% 2px;
    border-left: 2px solid ${props => props.theme.body};
    border-right: 2px solid ${props => props.theme.text};
    z-index:1;
`

const SubBox = styled.div`
width: 50%;
position: relative;
display: flex;

.pic {
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translate(-50%, 0);
    width: 100%;
    height: auto;
}
`

const Text = styled.div`
font-size: calc(0.9em + 1.5vw);
color: ${props => props.theme.body};
padding: 2rem;
cursor: auto;

display: flex;
flex-direction: column;
justify-content: center;

&>*:nth-last-child(2){
    color: ${props => `rgba(${props.theme.textRgba},0.75)` };
    font-size: calc(0.35rem + 1.5vw);
    font-weight: 300;
    margin-top: 1.5rem
}

&>*:nth-last-child(1){
    color: ${props => `rgba(${props.theme.textRgba},0.75)` };
    font-size: calc(0.35rem + 1.5vw);
    font-weight: 300;
    margin-top: 0.4rem
}
`

const Intro = () => {
    return (
        <Box
        initial={{height:0}}
        animate={{height:'55vh'}}
        transition={{type:'spring', duration:2, delay:1 }}
        >
            <SubBox>
                <Text>
                    <h1>Hey!</h1>
                    <h2>I'm Eugene.</h2>
                    <h6>I'm a software engineering student @ the University of Auckland.</h6>
                    <h6>I've worked with:</h6>

                </Text>
            </SubBox>
            <SubBox>
                <motion.div
                initial={{opacity:0}}
                animate={{opacity:1}}
                transition={{duration:1, delay:2 }}>
                    {/*<img className="pic" src={Me} alt="Profile Pic"/>*/}
                </motion.div>
            </SubBox>
        </Box>
    )
}

export default Intro
