import { React, useState, handleClick } from 'react'
import styled, { keyframes } from 'styled-components'

import PowerButton from '../subComponents/PowerButton'
import LogoComponent from '../subComponents/LogoComponent'
import SocialIcons from '../subComponents/SocialIcons'
import { NavLink } from 'react-router-dom'
import { YinYang } from './AllSvgs'

const MainContainer = styled.div`
background: ${props => props.theme.body};
width: 100vw;
height: 100vh;
overflow: hidden;
position: relative;

h1, h2, h3, h4, h5, h6 {
    font-family: 'Karla', sans-serif;
    font-weight: 500;
}
`

const Container = styled.div`
padding: 2rem;
`
const Contact = styled(NavLink)`
color: ${props => props.theme.text};
position: absolute;
top: 2rem;
right: calc(1rem + 2vw);
text-decoration: none;
z-index: 1;
`

const Blog = styled(NavLink)`
color: ${props => props.theme.text};
position: absolute;
top: 50%;
right: calc(1rem + 2vw);
transform: rotate(90deg) translate(-50%, -50%);
text-decoration: none;
z-index: 1;
`

const Work = styled(NavLink)`
color: ${props => props.theme.text};
position: absolute;
top: 50%;
left: calc(1rem + 2vw);
transform: translate(-50%, -50%) rotate(-90deg);
text-decoration: none;
z-index: 1;
`

const BottomBar = styled.div`
position: absolute;
bottom: 2rem;
left: 0;
right: 0;
width: 100%;

display: flex;
justify-content: space-evenly;
`

const About = styled(NavLink)`
color: ${props => props.theme.text};
text-decoration: none;
z-index: 1;
`

const Skills = styled(NavLink)`
color: ${props => props.theme.text};
text-decoration: none;
z-index: 1;
`
const rotate = keyframes`
from {
    transform: rotate(0);
}
to {
    transform: rotate(360deg);
}
`

const Center = styled.button`
position: absolute;
top: ${props => props.click ? '85%' :'50%' };
left: ${props => props.click ? '92%' :'50%' };
transform: translate(-50%, -50%);
border: none;
outline: none;
background-color: transparent;
cursor: pointer;

display: flex;
flex-direction: column;
justify-content: center;
align-items: center;

&>:first-child {
    animation: ${rotate} infinite 4s linear;
}

&>:last-child {
    padding-top: 1rem;
}
`

const Main = () => {

    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click);

    return (
        <MainContainer>
            <Container>
                <PowerButton/>
                <LogoComponent/>
                <SocialIcons/>

                <Center click={click}>
                    <YinYang onclick={()=> handleClick()} width={200} height={200} fill='currentColor'/>
                    <span>click here</span>
                </Center>

                <Contact target="_blank" to={{pathname:"mailto:luorixo@gmail.com"}}>
                    <h2>
                        Say hi...
                    </h2>
                </Contact>
                <Blog to={{pathname:"/blog"}}>
                    <h2>
                        Blog
                    </h2>
                </Blog>
                <Work to={{pathname:"/work"}}>
                    <h2>
                        Work
                    </h2>
                </Work>

                <BottomBar>
                <About to={{pathname:"/about"}}>
                    <h2>
                        About.
                    </h2>
                </About>
                <Skills to={{pathname:"/skills"}}>
                    <h2>
                        Skills.
                    </h2>
                </Skills>
                </BottomBar>


            </Container>
        </MainContainer>
    )
}

export default Main
