// dark and light themes

// Dark Green: #8aa92c
// Light Green: #C2DC71
// Light Yellow: #FFFAE0
// Bright Yellow: #ffd608
// Brown: #631F0F


export const lightTheme = {
    body:"#fff",
    text:"#C2DC71",
    fontFamily:"'Source Sans Pro', sans-serif",
    bodyRgba : "252, 246, 244",
    textRgba:"0,0,0",
}

export const darkTheme = {
    body:"#C2DC71",
    text:"#fff",
    fontFamily:"'Source Sans Pro', sans-serif",
    bodyRgba : "0,0,0",
    textRgba:"252, 246, 244",
}
